import React, { useEffect, useMemo, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Dropdown } from "primereact/dropdown";
import { InputSwitch } from "primereact/inputswitch";
import { Button } from "primereact/button";
import { ConfirmDialog, confirmDialog } from "primereact/confirmdialog";
import { http, buildQuery } from "../lib/api";

export default function CatalogsPage({ baseUrl, onToast }) {
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [categories, setCategories] = useState([]);
  const [filterCatId, setFilterCatId] = useState(null);
  const [minPrice, setMinPrice] = useState(null);
  const [maxPrice, setMaxPrice] = useState(null);

  const [dialogOpen, setDialogOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ name: "", code: "", categoryId: 0, price: 0, imageUrl: "", description: "", isActive: true });

  const catOptions = useMemo(() => categories.map(c => ({ label: `${c.name} (#${c.id})`, value: c.id })), [categories]);
  const catName = (id) => categories.find(c => c.id === id)?.name || id;

  const loadCategories = async () => {
    try {
      const data = await http(baseUrl, "/api/categories" + buildQuery({ page: 1, size: 999 }));
      setCategories(data.items || []);
    } catch (err) { onToast("warn", "Không tải được danh mục", err.message); }
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await http(baseUrl, "/api/catalogs" + buildQuery({ page, size, q, categoryId: filterCatId, minPrice, maxPrice }));
      setRows(data.items || []);
      setTotal(data.total || 0);
    } catch (err) { onToast("error", "Tải catalogs thất bại", err.message); }
    finally { setLoading(false); }
  };

  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [page, size]);

  const onSearch = () => { setPage(1); fetchData(); };

  const openNew = () => {
    setEditId(null);
    setForm({ name: "", code: "", categoryId: categories[0]?.id ?? 0, price: 0, imageUrl: "", description: "", isActive: true });
    setDialogOpen(true);
  };
  const openEdit = (r) => {
    setEditId(r.id);
    setForm({ name: r.name, code: r.code, categoryId: r.categoryId, price: r.price, imageUrl: r.imageUrl || "", description: r.description || "", isActive: !!r.isActive });
    setDialogOpen(true);
  };

  const save = async () => {
    try {
      if (!form.name?.trim() || !form.code?.trim()) return onToast("warn", "Thiếu dữ liệu", "Tên và Code bắt buộc");
      if (!form.categoryId) return onToast("warn", "Thiếu dữ liệu", "Chọn Category");
      const body = JSON.stringify({ ...form, price: Number(form.price) || 0 });
      if (editId == null) {
        await http(baseUrl, "/api/catalogs", { method: "POST", body });
        onToast("success", "Đã tạo", form.name);
      } else {
        await http(baseUrl, `/api/catalogs/${editId}`, { method: "PUT", body });
        onToast("success", "Đã cập nhật", form.name);
      }
      setDialogOpen(false);
      fetchData();
    } catch (err) { onToast("error", "Lưu thất bại", err.message); }
  };

  const remove = (r) => {
    confirmDialog({
      message: `Xoá sản phẩm "${r.name}"?`, header: "Xác nhận", icon: "pi pi-exclamation-triangle",
      acceptClassName: "p-button-danger",
      accept: async () => {
        try {
          await http(baseUrl, `/api/catalogs/${r.id}`, { method: "DELETE" });
          onToast("success", "Đã xoá", r.name);
          fetchData();
        } catch (err) { onToast("error", "Xoá thất bại", err.message); }
      }
    });
  };

  return (
    <div className="flex flex-column gap-3">
      <ConfirmDialog />
      <div className="flex flex-wrap gap-2 align-items-center">
        <span className="p-input-icon-left">
          <i className="pi pi-search" />
          <InputText value={q} onChange={(e) => setQ(e.target.value)} placeholder="Tìm theo tên/slug/code" onKeyDown={(e)=> e.key==='Enter' && onSearch()} />
        </span>
        <Dropdown value={filterCatId} options={[{label:"(Tất cả)", value:null}, ...catOptions]} onChange={(e)=> setFilterCatId(e.value)} placeholder="Danh mục" className="w-15rem" showClear />
        <span className="flex align-items-center gap-2">
          <span className="text-600">Giá từ</span>
          <InputNumber value={minPrice} onValueChange={(e)=> setMinPrice(e.value)} mode="currency" currency="USD" locale="en-US" className="w-12rem" />
          <span className="text-600">đến</span>
          <InputNumber value={maxPrice} onValueChange={(e)=> setMaxPrice(e.value)} mode="currency" currency="USD" locale="en-US" className="w-12rem" />
        </span>
        <Button icon="pi pi-filter" label="Lọc" onClick={onSearch} />
        <Button icon="pi pi-refresh" className="p-button-text" onClick={fetchData} />
        <div className="flex-1" />
        <Button icon="pi pi-plus" label="Thêm" onClick={openNew} />
      </div>

      <DataTable value={rows} loading={loading} paginator rows={size} totalRecords={total}
                 first={(page - 1) * size} onPage={(e) => { setPage(Math.floor(e.first / e.rows) + 1); setSize(e.rows); }}
                 rowsPerPageOptions={[5,10,20,50]} responsiveLayout="scroll" className="shadow-1 border-round-xl">
        <Column field="id" header="#" style={{ width: 70 }} />
        <Column field="name" header="Tên" sortable />
        <Column field="code" header="Code" sortable style={{ width: 140 }} />
        <Column header="Danh mục" body={(r) => catName(r.categoryId)} />
        <Column field="price" header="Giá" body={(r) => r.price?.toLocaleString(undefined, { style: 'currency', currency: 'USD' })} style={{ width: 160 }} />
        <Column header="Trạng thái" body={(r) => r.isActive ? <span className="text-green-600">Active</span> : <span className="text-600">Inactive</span>} style={{ width: 130 }} />
        <Column header="Thao tác" style={{ width: 160 }} body={(r) => (
          <div className="flex gap-2">
            <Button icon="pi pi-pencil" className="p-button-sm p-button-text" onClick={() => openEdit(r)} />
            <Button icon="pi pi-trash" className="p-button-sm p-button-text p-button-danger" onClick={() => remove(r)} />
          </div>
        )} />
      </DataTable>

      <Dialog header={editId == null ? "Thêm sản phẩm" : "Sửa sản phẩm"} visible={dialogOpen} style={{ width: "42rem" }} modal onHide={() => setDialogOpen(false)}>
        <div className="flex flex-column gap-3">
          <div className="flex flex-column gap-1">
            <label className="font-medium">Tên *</label>
            <InputText value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
          </div>
          <div className="flex gap-3">
            <span className="flex-1">
              <label className="block mb-1 font-medium">Code *</label>
              <InputText value={form.code} onChange={(e) => setForm({ ...form, code: e.target.value })} />
            </span>
            <span className="flex-1">
              <label className="block mb-1 font-medium">Danh mục *</label>
              <Dropdown value={form.categoryId} options={catOptions} onChange={(e)=> setForm({ ...form, categoryId: e.value })} className="w-full" />
            </span>
          </div>
          <div className="flex gap-3">
            <span className="flex-1">
              <label className="block mb-1 font-medium">Giá</label>
              <InputNumber value={form.price} onValueChange={(e)=> setForm({ ...form, price: e.value ?? 0 })} mode="currency" currency="USD" locale="en-US" className="w-full" />
            </span>
            <span className="flex align-items-center gap-2">
              <label className="font-medium">Kích hoạt</label>
              <InputSwitch checked={form.isActive} onChange={(e) => setForm({ ...form, isActive: e.value })} />
            </span>
          </div>
          <div className="flex flex-column gap-1">
            <label className="font-medium">Ảnh (URL)</label>
            <InputText value={form.imageUrl} onChange={(e)=> setForm({ ...form, imageUrl: e.target.value })} placeholder="https://..." />
          </div>
          <div className="flex flex-column gap-1">
            <label className="font-medium">Mô tả</label>
            <InputText value={form.description} onChange={(e)=> setForm({ ...form, description: e.target.value })} />
          </div>
        </div>
        <div className="flex justify-content-end gap-2 mt-4">
          <Button label="Huỷ" className="p-button-text" onClick={() => setDialogOpen(false)} />
          <Button label="Lưu" icon="pi pi-save" onClick={save} />
        </div>
      </Dialog>
    </div>
  );
}
