// src/pages/Catalogs.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Dropdown } from "primereact/dropdown";
import { InputSwitch } from "primereact/inputswitch";
import { Button } from "primereact/button";
import { ConfirmDialog, confirmDialog } from "primereact/confirmdialog";
import { Tag } from "primereact/tag";
import { Panel } from "primereact/panel";
import { Toolbar } from "primereact/toolbar";
import { Chip } from "primereact/chip";
import { Divider } from "primereact/divider";
import { InputTextarea } from "primereact/inputtextarea";
import { FloatLabel } from "primereact/floatlabel";
import { Card } from "primereact/card";

import { createApi, getAxiosMessage } from "../lib/api";
import { MAX_IMAGEURL, normalizeImgInput, previewUrl } from "../lib/img";

const THUMB = { width: 64, height: 64, objectFit: "cover", borderRadius: 6 };

export default function CatalogsPage({ baseUrl, onToast }) {
  const api = useMemo(() => createApi(baseUrl), [baseUrl]);

  // Filters & table state
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  // Category for filter & form
  const [categories, setCategories] = useState([]);

  // Dialog state
  const [dialogOpen, setDialogOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    name: "",
    code: "",
    categoryId: 0,
    price: 0,
    imageUrl: "",
    description: "",
    isActive: true,
  });

  const dt = useRef(null);

  const catOptions = useMemo(
    () => categories.map((c) => ({ label: `${c.name} (#${c.id})`, value: c.id })),
    [categories]
  );
  const catName = (id) => categories.find((c) => c.id === id)?.name || id;

  /* -------------------- API calls -------------------- */
  const loadCategories = async () => {
    try {
      const { data } = await api.get("/api/categories", { params: { page: 1, size: 999 } });
      setCategories(data?.items || []);
    } catch (err) {
      onToast("warn", "Không tải được danh mục", getAxiosMessage(err));
    }
  };

  const loadingData = async () => {
    setLoading(true);
    try {
      const { data } = await api.get("/api/catalogs", {
        params: { page, size, q },
      });
      setRows(data?.items || []);
      setTotal(data?.total || 0);
    } catch (err) {
      onToast("error", "Tải catalogs thất bại", getAxiosMessage(err));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { loadingData(); /* eslint-disable-next-line */ }, [page, size]);

  const onSearch = () => { setPage(1); loadingData(); };
  const onClear = () => { setQ(""); setPage(1); loadingData(); };

  /* -------------------- CRUD actions -------------------- */
  const openNew = () => {
    setEditId(null);
    setForm({
      name: "",
      code: "",
      categoryId: categories[0]?.id ?? 0,
      price: 0,
      imageUrl: "",
      description: "",
      isActive: true,
    });
    setDialogOpen(true);
  };

  const openEdit = (r) => {
    setEditId(r.id);
    setForm({
      name: r.name,
      code: r.code,
      categoryId: r.categoryId,
      price: r.price,
      imageUrl: r.imageUrl || "",
      description: r.description || "",
      isActive: !!r.isActive,
    });
    setDialogOpen(true);
  };

  const save = async () => {
    if (!form.name?.trim() || !form.code?.trim()) {
      return onToast("warn", "Thiếu dữ liệu", "Tên và Code bắt buộc");
    }
    if (!form.categoryId) {
      return onToast("warn", "Thiếu dữ liệu", "Chọn Category");
    }

    // Chuẩn hoá ảnh: cho phép thiếu http/https, //domain..., domain trần, đường dẫn tương đối
    let img = "";
    try {
      img = normalizeImgInput(form.imageUrl);
    } catch (e) {
      const msg =
        e.message === "no_data_uri" ? "Không hỗ trợ ảnh base64 (data:)." :
        e.message === "no_local_path" ? "Không dùng được đường dẫn cục bộ (file://, C:\\...)." :
        e.message === "too_long" ? `Link ảnh quá dài (tối đa ${MAX_IMAGEURL} ký tự).` :
        e.message === "unsupported_scheme" ? "Chỉ hỗ trợ http/https." :
        "Ảnh không hợp lệ.";
      return onToast("warn", "Ảnh không hợp lệ", msg);
    }

    try {
      const payload = { ...form, imageUrl: img || null, price: Number(form.price) || 0 };
      if (editId == null) {
        await api.post("/api/catalogs", payload);
        onToast("success", "Đã tạo", form.name);
      } else {
        await api.put(`/api/catalogs/${editId}`, payload);
        onToast("success", "Đã cập nhật", form.name);
      }
      setDialogOpen(false);
      loadingData();
    } catch (err) {
      onToast("error", "Lưu thất bại", getAxiosMessage(err));
    }
  };

  const remove = (r) =>
    confirmDialog({
      message: `Xoá sản phẩm "${r.name}"?`,
      header: "Xác nhận",
      icon: "pi pi-exclamation-triangle",
      acceptClassName: "p-button-danger",
      accept: async () => {
        try {
          await api.delete(`/api/catalogs/${r.id}`);
          onToast("success", "Đã xoá", r.name);
          loadingData();
        } catch (err) {
          onToast("error", "Xoá thất bại", getAxiosMessage(err));
        }
      },
    });

  const toggleActive = async (r) => {
    try {
      const payload = {
        name: r.name,
        code: r.code,
        categoryId: r.categoryId,
        price: r.price,
        imageUrl: r.imageUrl || "",
        description: r.description || "",
        isActive: !r.isActive,
      };
      await api.put(`/api/catalogs/${r.id}`, payload);
      onToast("success", !r.isActive ? "Đã kích hoạt" : "Đã tắt", r.name);
      loadingData();
    } catch (err) {
      onToast("error", "Đổi trạng thái thất bại", getAxiosMessage(err));
    }
  };

  /* -------------------- Header / filters -------------------- */
  const HeaderToolbar = (
    <Toolbar
      left={() => (
        <div className="flex align-items-center gap-2">
          <span className="p-input-icon-left">
            <i className="pi pi-search" />
            <InputText
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Tìm theo tên/slug/code"
              onKeyDown={(e) => e.key === "Enter" && onSearch()}
              style={{ minWidth: 280 }}
            />
          </span>
          {q && <Chip label={`Tìm: ${q}`} onRemove={onClear} removable />}
          <Button icon="pi pi-search" label="Tìm" onClick={onSearch} />
          <Button icon="pi pi-plus" label="Thêm" onClick={openNew} />
        </div>
      )}
      right={() => (
        <div>
          <Button
            icon="pi pi-download"
            label="Export CSV"
            onClick={() => dt.current?.exportCSV()}
          />
        </div>
      )}
    />
  );

  /* -------------------- Render -------------------- */
  return (
    <div>
      <ConfirmDialog />

      {HeaderToolbar}

      <Panel header="Danh sách sản phẩm">
        <DataTable
          ref={dt}
          value={rows}
          loading={loading}
          paginator
          lazy
          rows={size}
          totalRecords={total}
          first={(page - 1) * size}
          onPage={(e) => {
            setPage(Math.floor(e.first / e.rows) + 1);
            setSize(e.rows);
          }}
          rowsPerPageOptions={[5, 10, 20, 50]}
          emptyMessage="Không có dữ liệu"
        >
          <Column field="id" header="#" style={{ width: 80 }} />

          <Column
            header="Ảnh"
            body={(r) =>
              r.imageUrl ? (
                <img src={previewUrl(r.imageUrl)} alt={r.name} style={THUMB} />
              ) : (
                <span className="pi pi-image" />
              )
            }
            style={{ width: 90 }}
          />

          <Column
            field="name"
            header="Tên"
            body={(r) => (
              <div>
                <div className="font-medium">{r.name}</div>
                {r.description && <small title={r.description}>{r.description}</small>}
              </div>
            )}
          />
          <Column field="code" header="Code" style={{ width: 140 }} />
          <Column header="Danh mục" body={(r) => catName(r.categoryId)} style={{ width: 200 }} />
          <Column
            field="price"
            header="Giá"
            body={(r) => r.price?.toLocaleString(undefined, { style: "currency", currency: "USD" })}
            style={{ width: 150 }}
          />
          <Column
            header="Trạng thái"
            body={(r) => (
              <Tag
                value={r.isActive ? "Active" : "Inactive"}
                severity={r.isActive ? "success" : "secondary"}
                onClick={() => toggleActive(r)}
              />
            )}
            style={{ width: 140 }}
          />
          <Column
            header="Thao tác"
            body={(r) => (
              <span>
                <Button icon="pi pi-pencil" className="p-button-text" onClick={() => openEdit(r)} />
                <Button
                  icon="pi pi-trash"
                  className="p-button-text p-button-danger"
                  onClick={() => remove(r)}
                />
              </span>
            )}
            style={{ width: 150 }}
          />
        </DataTable>
      </Panel>

      <Dialog
        visible={dialogOpen}
        onHide={() => setDialogOpen(false)}
        modal
        header={<div style={{ fontWeight: 600, fontSize: "1.3rem" }}>{editId == null ? "Thêm sản phẩm" : "Sửa sản phẩm"}</div>}
        style={{ width: "60rem", maxWidth: "95vw", borderRadius: 12 }}
        breakpoints={{ "960px": "70vw", "640px": "95vw" }}
        footer={
          <div className="flex gap-3 justify-content-end" style={{ padding: "1rem" }}>
            <Button label="Huỷ" className="p-button-text" onClick={() => setDialogOpen(false)} />
            <Button label="Lưu" icon="pi pi-save" onClick={save} />
          </div>
        }
      >
        <div className="p-fluid grid formgrid" style={{ gap: "1.5rem" }}>
          <div className="col-12">
            <Divider align="left">
              <span className="flex align-items-center gap-2">
                <i className="pi pi-info-circle" />
                <span>Thông tin cơ bản</span>
              </span>
            </Divider>
          </div>
          <div className="field col-12 md:col-8">
            <FloatLabel>
              <InputText
                id="name"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
              />
              <label htmlFor="name">Tên *</label>
            </FloatLabel>
          </div>
          <div className="field col-12 md:col-4">
            <FloatLabel>
              <InputText
                id="code"
                value={form.code}
                onChange={(e) => setForm({ ...form, code: e.target.value })}
              />
              <label htmlFor="code">Code *</label>
            </FloatLabel>
          </div>
          <div className="field col-12 md:col-6">
            <FloatLabel>
              <Dropdown
                id="categoryId"
                inputId="categoryId"
                value={form.categoryId}
                options={catOptions}
                onChange={(e) => setForm({ ...form, categoryId: e.value })}
                className="w-full"
              />
              <label htmlFor="categoryId">Danh mục *</label>
            </FloatLabel>
          </div>

          <div className="col-12">
            <Divider align="left">
              <span className="flex align-items-center gap-2">
                <i className="pi pi-sliders-h" />
                <span>Giá & trạng thái</span>
              </span>
            </Divider>
          </div>
          <div className="field col-12 md:col-6">
            <label className="block mb-1">Giá</label>
            <InputNumber
              value={form.price}
              onValueChange={(e) => setForm({ ...form, price: e.value ?? 0 })}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-full"
            />
          </div>
          <div className="field col-12 md:col-6">
            <label className="block mb-2">Kích hoạt</label>
            <div className="flex align-items-center gap-3">
              <InputSwitch
                checked={form.isActive}
                onChange={(e) => setForm({ ...form, isActive: e.value })}
              />
              <Tag
                value={form.isActive ? "Active" : "Inactive"}
                severity={form.isActive ? "success" : "secondary"}
              />
            </div>
          </div>

          <div className="col-12">
            <Divider align="left">
              <span className="flex align-items-center gap-2">
                <i className="pi pi-image" />
                <span>Hình ảnh & mô tả</span>
              </span>
            </Divider>
          </div>
          <div className="field col-12 md:col-8">
            <FloatLabel>
              <InputText
                id="imageUrl"
                value={form.imageUrl}
                maxLength={MAX_IMAGEURL}
                onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
                onBlur={(e) => setForm({ ...form, imageUrl: e.target.value.trim() })}
                placeholder="example.com/img.jpg hoặc //domain/img.png hoặc images/a.jpg"
              />
              <label htmlFor="imageUrl">Ảnh (URL)</label>
            </FloatLabel>
            <small className="text-600">Cho phép thiếu http/https; không hỗ trợ ảnh base64 (data:).</small>
          </div>
          <div className="field col-12 md:col-4">
            <Card subTitle={form.code || "—"}>
              <div className="flex align-items-center justify-content-center" style={{ height: 160, borderRadius: 8, overflow: "hidden" }}>
                {form.imageUrl ? (
                  <img
                    src={previewUrl(form.imageUrl)}
                    alt="preview"
                    style={{ width: 160, height: 160, objectFit: "cover", borderRadius: 8 }}
                  />
                ) : (
                  <span className="pi pi-image text-500" style={{ fontSize: 24 }} />
                )}
              </div>
            </Card>
          </div>
          <div className="field col-12">
            <FloatLabel>
              <InputTextarea
                id="description"
                value={form.description}
                onChange={(e) => setForm({ ...form, description: e.target.value })}
                autoResize
                rows={3}
              />
              <label htmlFor="description">Mô tả</label>
            </FloatLabel>
          </div>
        </div>
      </Dialog>
    </div>
  );
}
