import React, { useRef } from "react";
import { Routes, Route, Navigate, Link, useLocation } from "react-router-dom";
import { Card } from "primereact/card";
import { Toast } from "primereact/toast";
import TopBar from "./components/TopBar.jsx";
import CategoriesPage from "./pages/Categories.jsx";
import CatalogsPage from "./pages/Catalogs.jsx";
import { useBaseUrl } from "./lib/useBaseUrl.js";

export default function App() {
  const toast = useRef(null);
  const { baseUrl, setBaseUrl } = useBaseUrl();
  const onToast = (severity, summary, detail) => toast.current?.show({ severity, summary, detail, life: 3000 });

  return (
    <div className="p-3 max-w-7xl mx-auto">
      <Toast ref={toast} />
      <TopBar baseUrl={baseUrl} setBaseUrl={setBaseUrl} onToast={onToast} />

      <NavTabs />

      <Card className="border-round-xl shadow-1">
        <Routes>
          <Route path="/" element={<Navigate to="/categories" replace />} />
          <Route path="/categories" element={<CategoriesPage baseUrl={baseUrl} onToast={onToast} />} />
          <Route path="/catalogs" element={<CatalogsPage baseUrl={baseUrl} onToast={onToast} />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </Card>
    </div>
  );
}

function NavTabs() {
  const loc = useLocation();
  const isActive = (p) => loc.pathname.startsWith(p);
  return (
    <div className="flex gap-2 mb-3">
      <Link to="/categories" className={`p-button p-component ${isActive('/categories') ? '' : 'p-button-outlined'}`}>
        <span className="pi pi-list mr-2" /> Categories
      </Link>
      <Link to="/catalogs" className={`p-button p-component ${isActive('/catalogs') ? '' : 'p-button-outlined'}`}>
        <span className="pi pi-box mr-2" /> Catalogs
      </Link>
    </div>
  );
}

function NotFound(){
  return <div className="p-4 text-600">Không tìm thấy trang.</div>;
}
