import React, { useEffect, useMemo, useRef, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Dropdown } from "primereact/dropdown";
import { InputSwitch } from "primereact/inputswitch";
import { Button } from "primereact/button";
import { ConfirmDialog, confirmDialog } from "primereact/confirmdialog";
import { Tag } from "primereact/tag";
import { Panel } from "primereact/panel";
import { Image } from "primereact/image";
import { Toolbar } from "primereact/toolbar";
import { Avatar } from "primereact/avatar";
import { Chip } from "primereact/chip";
import { SpeedDial } from "primereact/speeddial";
import { Divider } from "primereact/divider";
import { InputTextarea } from "primereact/inputtextarea"; // ✅ ĐÚNG
import { FloatLabel } from "primereact/floatlabel";
import { Card } from "primereact/card";
// ❌ Bỏ InputGroup/InputGroupAddon vì version của bạn chưa có
// import { InputGroup } from "primereact/inputgroup";
// import { InputGroupAddon } from "primereact/inputgroupaddon";

import { http, buildQuery } from "../lib/api";

export default function CatalogsPage({ baseUrl, onToast }) {
  // ------------------ State ------------------
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [categories, setCategories] = useState([]);
  const [filterCatId, setFilterCatId] = useState(null);
  const [minPrice, setMinPrice] = useState(null);
  const [maxPrice, setMaxPrice] = useState(null);

  const [dialogOpen, setDialogOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    name: "",
    code: "",
    categoryId: 0,
    price: 0,
    imageUrl: "",
    description: "",
    isActive: true,
  });

  // refs
  const dt = useRef(null);

  // ------------------ Memos/helpers ------------------
  const catOptions = useMemo(
    () => categories.map((c) => ({ label: `${c.name} (#${c.id})`, value: c.id })),
    [categories]
  );
  const catName = (id) => categories.find((c) => c.id === id)?.name || id;

  const hasAnyFilter = !!(q || filterCatId !== null || minPrice != null || maxPrice != null);

  const clearFilter = (key) => {
    switch (key) {
      case "q": setQ(""); break;
      case "cat": setFilterCatId(null); break;
      case "min": setMinPrice(null); break;
      case "max": setMaxPrice(null); break;
      default: break;
    }
    setPage(1);
    setTimeout(fetchData, 0);
  };

  // ------------------ API calls ------------------
  const loadCategories = async () => {
    try {
      const data = await http(baseUrl, "/api/categories" + buildQuery({ page: 1, size: 999 }));
      setCategories(data.items || []);
    } catch (err) {
      onToast("warn", "Không tải được danh mục", err.message);
    }
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await http(
        baseUrl,
        "/api/catalogs" +
          buildQuery({ page, size, q, categoryId: filterCatId, minPrice, maxPrice })
      );
      setRows(data.items || []);
      setTotal(data.total || 0);
    } catch (err) {
      onToast("error", "Tải catalogs thất bại", err.message);
    } finally {
      setLoading(false);
    }
  };

  // ------------------ Effects ------------------
  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [page, size]);

  // ------------------ Actions ------------------
  const onSearch = () => { setPage(1); fetchData(); };

  const openNew = () => {
    setEditId(null);
    setForm({
      name: "",
      code: "",
      categoryId: categories[0]?.id ?? 0,
      price: 0,
      imageUrl: "",
      description: "",
      isActive: true,
    });
    setDialogOpen(true);
  };

  const openEdit = (r) => {
    setEditId(r.id);
    setForm({
      name: r.name,
      code: r.code,
      categoryId: r.categoryId,
      price: r.price,
      imageUrl: r.imageUrl || "",
      description: r.description || "",
      isActive: !!r.isActive,
    });
    setDialogOpen(true);
  };

  const save = async () => {
    if (!form.name?.trim() || !form.code?.trim()) {
      return onToast("warn", "Thiếu dữ liệu", "Tên và Code bắt buộc");
    }
    if (!form.categoryId) {
      return onToast("warn", "Thiếu dữ liệu", "Chọn Category");
    }

    try {
      const body = JSON.stringify({ ...form, price: Number(form.price) || 0 });
      if (editId == null) {
        await http(baseUrl, "/api/catalogs", { method: "POST", body });
        onToast("success", "Đã tạo", form.name);
      } else {
        await http(baseUrl, `/api/catalogs/${editId}`, { method: "PUT", body });
        onToast("success", "Đã cập nhật", form.name);
      }
      setDialogOpen(false);
      fetchData();
    } catch (err) {
      onToast("error", "Lưu thất bại", err.message);
    }
  };

  const remove = (r) =>
    confirmDialog({
      message: `Xoá sản phẩm "${r.name}"?`,
      header: "Xác nhận",
      icon: "pi pi-exclamation-triangle",
      acceptClassName: "p-button-danger",
      accept: async () => {
        try {
          await http(baseUrl, `/api/catalogs/${r.id}`, { method: "DELETE" });
          onToast("success", "Đã xoá", r.name);
          fetchData();
        } catch (err) {
          onToast("error", "Xoá thất bại", err.message);
        }
      },
    });

  const toggleActive = async (r) => {
    try {
      const body = JSON.stringify({
        name: r.name,
        code: r.code,
        categoryId: r.categoryId,
        price: r.price,
        imageUrl: r.imageUrl || "",
        description: r.description || "",
        isActive: !r.isActive
      });
      await http(baseUrl, `/api/catalogs/${r.id}`, { method: "PUT", body });
      onToast("success", !r.isActive ? "Đã kích hoạt" : "Đã tắt", r.name);
      fetchData();
    } catch (err) {
      onToast("error", "Đổi trạng thái thất bại", err.message);
    }
  };

  // ------------------ UI pieces ------------------
   const HeaderToolbar = (
    <Toolbar
      className="surface-card border-round-3xl shadow-3 mb-4 px-4 py-3"
      left={() => (
        <div className="flex flex-wrap gap-3 align-items-center">
          <span className="p-input-icon-left flex-grow-1 min-w-[16rem]">
            <i className="pi pi-search"></i>
            <InputText
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Tìm theo tên/slug/code"
              onKeyDown={(e) => e.key === "Enter" && onSearch()}
              className="w-full"
              style={{ minWidth: 200 }}
              aria-label="Search"
            />
          </span>
          <Dropdown
            value={filterCatId}
            options={[{ label: "(Tất cả)", value: null }, ...catOptions]}
            onChange={(e) => setFilterCatId(e.value)}
            placeholder="Danh mục"
            className="w-40 md:w-60"
            showClear
            aria-label="Lọc danh mục"
          />
          <div className="flex flex-wrap gap-3 align-items-center">
            <span className="flex align-items-center gap-1 text-600 font-semibold">Giá từ</span>
            <InputNumber
              value={minPrice}
              onValueChange={(e) => setMinPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-28 md:w-40"
              showButtons
              buttonLayout="stack"
              aria-label="Giá tối thiểu"
            />
            <span className="flex align-items-center gap-1 text-600 font-semibold">đến</span>
            <InputNumber
              value={maxPrice}
              onValueChange={(e) => setMaxPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-28 md:w-40"
              showButtons
              buttonLayout="stack"
              aria-label="Giá tối đa"
            />
          </div>
          <Button icon="pi pi-filter" label="Lọc" onClick={onSearch} className="p-button-lg" />
          <Button icon="pi pi-refresh" className="p-button-text p-button-lg" onClick={fetchData} aria-label="Làm mới dữ liệu" />
        </div>
      )}
      right={() => (
        <div className="flex gap-3">
          <Button icon="pi pi-download" label="Xuất CSV" outlined onClick={() => dt.current?.exportCSV()} className="p-button-lg" />
          <Button icon="pi pi-plus" label="Thêm sản phẩm" onClick={openNew} className="p-button-lg" />
        </div>
      )}
    />
  );

  const ActiveFilterChips = hasAnyFilter && (
    <div className="flex flex-wrap gap-2 mb-4 px-3">
      {q && <Chip label={`Tìm: ${q}`} icon="pi pi-times" onIconClick={() => clearFilter("q")} removable className="p-chip-lg" />}
      {filterCatId !== null && (
        <Chip label={`Danh mục: ${catName(filterCatId)}`} icon="pi pi-times" onIconClick={() => clearFilter("cat")} removable className="p-chip-lg" />
      )}
      {minPrice != null && <Chip label={`Min: ${minPrice.toLocaleString("en-US", { style: "currency", currency: "USD" })}`} icon="pi pi-times" onIconClick={() => clearFilter("min")} removable className="p-chip-lg" />}
      {maxPrice != null && <Chip label={`Max: ${maxPrice.toLocaleString("en-US", { style: "currency", currency: "USD" })}`} icon="pi pi-times" onIconClick={() => clearFilter("max")} removable className="p-chip-lg" />}
      <Button
        size="small"
        className="p-button-text p-button-danger"
        onClick={() => {
          setQ("");
          setFilterCatId(null);
          setMinPrice(null);
          setMaxPrice(null);
          setPage(1);
          fetchData();
        }}
        aria-label="Xóa tất cả bộ lọc"
      >
        Xoá tất cả bộ lọc
      </Button>
    </div>
  );

  // SpeedDial Fab giữ nguyên kèm CSS tinh chỉnh
  const SpeedDialFab = (
    <SpeedDial
      className="speeddial-fixed shadow-3"
      radius={60}
      type="quarter-circle"
      direction="up-left"
      buttonClassName="p-button-rounded p-button-lg p-button-primary"
      model={[
        { label: "Thêm", icon: "pi pi-plus", command: openNew },
        { label: "Làm mới", icon: "pi pi-refresh", command: fetchData },
        { label: "Xuất CSV", icon: "pi pi-download", command: () => dt.current?.exportCSV() },
      ]}
      aria-label="Menu tác vụ nhanh"
    />
  );

  return (
    <div className="flex flex-column gap-4 surface-ground p-4">
      <ConfirmDialog />
      {HeaderToolbar}
      {ActiveFilterChips}

      <Panel header="Danh sách sản phẩm" className="surface-card border-round-3xl shadow-3 p-3">
        <DataTable
          ref={dt}
          value={rows}
          loading={loading}
          paginator
          rows={size}
          totalRecords={total}
          first={(page - 1) * size}
          onPage={(e) => {
            setPage(Math.floor(e.first / e.rows) + 1);
            setSize(e.rows);
          }}
          rowsPerPageOptions={[5, 10, 20, 50]}
          paginatorTemplate="RowsPerPageDropdown FirstPageLink PrevPageLink CurrentPageReport NextPageLink LastPageLink"
          currentPageReportTemplate="({first}–{last} / {totalRecords})"
          emptyMessage={
            <div className="text-center p-6 text-600 text-xl">
              <i className="pi pi-box mb-3 text-5xl" />
              Chưa có dữ liệu. Nhấn <b>Thêm sản phẩm</b> để tạo mới.
            </div>
          }
          showGridlines
          rowHover
          stripedRows
          responsiveLayout="scroll"
          className="border-round-3xl"
        >
          <Column field="id" header="#" style={{ width: 70 }} />
          <Column
            header="Ảnh"
            style={{ width: 100 }}
            body={(r) =>
              r.imageUrl ? (
                <Image
                  src={r.imageUrl}
                  alt={r.name}
                  imageStyle={{ width: 48, height: 48, objectFit: "cover", borderRadius: 10 }}
                  preview
                />
              ) : (
                <Avatar
                  icon="pi pi-image"
                  shape="square"
                  className="bg-gray-100 text-600"
                  style={{ width: 48, height: 48 }}
                />
              )
            }
          />
          <Column
            field="name"
            header="Tên"
            sortable
            body={(r) => (
              <div className="flex flex-column gap-1">
                <span className="font-semibold text-lg">{r.name}</span>
                {r.description && (
                  <small className="text-500" title={r.description}>
                    {r.description.length > 64 ? r.description.slice(0, 64) + "…" : r.description}
                  </small>
                )}
              </div>
            )}
          />
          <Column field="code" header="Code" sortable style={{ width: 160 }} />
          <Column
            header="Danh mục"
            body={(r) => <Tag value={catName(r.categoryId)} severity="info" rounded className="font-semibold" />}
          />
          <Column
            field="price"
            header="Giá"
            body={(r) => r.price?.toLocaleString(undefined, { style: "currency", currency: "USD" })}
            style={{ width: 160, textAlign: "right" }}
          />
          <Column
            header="Trạng thái"
            style={{ width: 160 }}
            body={(r) => (
              <Tag
                value={r.isActive ? "Active" : "Inactive"}
                severity={r.isActive ? "success" : "secondary"}
                rounded
                className="cursor-pointer"
                onClick={() => toggleActive(r)}
                title="Nhấn để đổi trạng thái"
              />
            )}
          />
          <Column
            header="Thao tác"
            style={{ width: 180 }}
            body={(r) => (
              <div className="flex gap-3 justify-content-end">
                <Button
                  icon="pi pi-pencil"
                  className="p-button-rounded p-button-text p-button-sm"
                  onClick={() => openEdit(r)}
                  tooltip="Sửa"
                />
                <Button
                  icon="pi pi-trash"
                  className="p-button-rounded p-button-text p-button-danger p-button-sm"
                  onClick={() => remove(r)}
                  tooltip="Xóa"
                />
              </div>
            )}
          />
        </DataTable>
      </Panel>

      {/* Dialog sửa/thêm */}
      <Dialog
        visible={dialogOpen}
        onHide={() => setDialogOpen(false)}
        modal
        draggable={false}
        header={
          <span className="flex align-items-center gap-3 text-primary font-semibold text-xl">
            <i className="pi pi-box" />
            {editId == null ? "Thêm sản phẩm" : "Sửa sản phẩm"}
          </span>
        }
        breakpoints={{ "1200px": "70vw", "768px": "90vw" }}
        style={{ width: "60rem" }}
        className="border-round-3xl shadow-4 surface-card"
        footer={
          <div className="flex justify-content-end gap-3 w-full">
            <Button label="Hủy" className="p-button-text p-button-lg" onClick={() => setDialogOpen(false)} />
            <Button label="Lưu" icon="pi pi-save" className="p-button-lg" onClick={save} />
          </div>
        }
      >
        <div className="grid">
          {/* Form inputs left */}
          <div className="col-12 md:col-7 flex flex-column gap-5 px-3">
            <Divider align="left" className="mb-4">
              <span className="text-700 font-bold flex align-items-center gap-2">
                <i className="pi pi-info-circle" /> Thông tin cơ bản
              </span>
            </Divider>

            <div className="grid gap-4">
              <div className="col-12">
                <FloatLabel>
                  <InputText
                    id="name"
                    value={form.name}
                    onChange={(e) => setForm({ ...form, name: e.target.value })}
                    className={!form.name?.trim() ? "p-invalid p-inputtext-lg" : "p-inputtext-lg"}
                    aria-describedby="name-help"
                    autoFocus
                  />
                  <label htmlFor="name">Tên *</label>
                </FloatLabel>
                {!form.name?.trim() && (
                  <small id="name-help" className="p-error">
                    Bắt buộc nhập tên sản phẩm
                  </small>
                )}
              </div>

              <div className="col-6">
                <FloatLabel>
                  <InputText
                    id="code"
                    value={form.code}
                    onChange={(e) => setForm({ ...form, code: e.target.value })}
                    className={!form.code?.trim() ? "p-invalid p-inputtext-lg" : "p-inputtext-lg"}
                    aria-describedby="code-help"
                  />
                  <label htmlFor="code">Code *</label>
                </FloatLabel>
                {!form.code?.trim() && (
                  <small id="code-help" className="p-error">
                    Bắt buộc nhập code
                  </small>
                )}
              </div>

              <div className="col-6">
                <FloatLabel>
                  <Dropdown
                    id="categoryId"
                    inputId="categoryId"
                    value={form.categoryId}
                    options={catOptions}
                    onChange={(e) => setForm({ ...form, categoryId: e.value })}
                    className={!form.categoryId ? "p-invalid w-full" : "w-full"}
                    aria-describedby="category-help"
                  />
                  <label htmlFor="categoryId">Danh mục *</label>
                </FloatLabel>
                {!form.categoryId && (
                  <small id="category-help" className="p-error">
                    Bắt buộc chọn danh mục
                  </small>
                )}
              </div>
            </div>

            <Divider align="left" type="dashed" className="my-5">
              <span className="text-700 font-bold flex align-items-center gap-2">
                <i className="pi pi-dollar" /> Giá & trạng thái
              </span>
            </Divider>

            <div className="grid gap-4 align-items-center">
              <div className="col-6">
                <label htmlFor="price" className="block mb-2 text-600 font-semibold">
                  Giá
                </label>
                <div className="p-inputgroup">
                  <span className="p-inputgroup-addon">
                    <i className="pi pi-dollar" />
                  </span>
                  <InputNumber
                    id="price"
                    value={form.price}
                    onValueChange={(e) => setForm({ ...form, price: e.value ?? 0 })}
                    mode="currency"
                    currency="USD"
                    locale="en-US"
                    className="w-full p-inputtext-lg"
                    inputClassName="p-inputtext-lg"
                    showButtons
                    buttonLayout="horizontal"
                  />
                </div>
              </div>

              <div className="col-6 flex align-items-center gap-3">
                <label className="text-600 font-semibold">Kích hoạt</label>
                <InputSwitch checked={form.isActive} onChange={(e) => setForm({ ...form, isActive: e.value })} />
                <Tag
                  value={form.isActive ? "Active" : "Inactive"}
                  severity={form.isActive ? "success" : "danger"}
                  rounded
                  className="ml-2 font-semibold"
                />
              </div>
            </div>

            <Divider align="left" className="mt-6 mb-4">
              <span className="text-700 font-bold flex align-items-center gap-2">
                <i className="pi pi-image" /> Hình ảnh & mô tả
              </span>
            </Divider>

            <div className="grid gap-4">
              <div className="col-12">
                <FloatLabel>
                  <InputText
                    id="imageUrl"
                    value={form.imageUrl}
                    onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
                    placeholder="https://your-image-url.com"
                    className="p-inputtext-lg"
                  />
                  <label htmlFor="imageUrl">Ảnh (URL)</label>
                </FloatLabel>
              </div>

              <div className="col-12">
                <FloatLabel>
                  <InputTextarea
                    id="description"
                    value={form.description}
                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                    autoResize
                    rows={4}
                    className="p-inputtextarea-lg"
                    aria-label="Mô tả sản phẩm"
                  />
                  <label htmlFor="description">Mô tả</label>
                </FloatLabel>
              </div>
            </div>
          </div>

          {/* Preview bên phải */}
          <div className="col-12 md:col-5">
            <Card title="Preview" subTitle={form.code || "—"} className="border-round-3xl shadow-2 surface-card">
              <div className="flex gap-5 align-items-center">
                <div
                  className="border-2 border-solid border-gray-300 border-round-lg overflow-hidden flex align-items-center justify-content-center"
                  style={{ width: 120, height: 120 }}
                >
                  {form.imageUrl ? (
                    <img src={form.imageUrl} alt="Preview" style={{ width: "100%", height: "100%", objectFit: "cover" }} />
                  ) : (
                    <i className="pi pi-image text-gray-400 text-6xl" />
                  )}
                </div>
                <div className="flex flex-column justify-content-center gap-2">
                  <h3 className="m-0 font-bold text-xl">{form.name || "Tên sản phẩm"}</h3>
                  <Tag
                    value={catOptions.find((c) => c.value === form.categoryId)?.label || "Danh mục"}
                    severity="info"
                    rounded
                    className="font-semibold"
                  />
                  <div className="text-lg font-semibold mt-4">
                    {(Number(form.price) || 0).toLocaleString(undefined, { style: "currency", currency: "USD" })}
                  </div>
                  <Tag
                    value={form.isActive ? "Active" : "Inactive"}
                    severity={form.isActive ? "success" : "danger"}
                    rounded
                    className="mt-3 font-semibold"
                  />
                </div>
              </div>
              {form.description && <p className="mt-4 text-gray-700">{form.description}</p>}
            </Card>
          </div>
        </div>
      </Dialog>

      {/* CSS SpeedDial cố định dưới góc phải màn hình */}
      <style>{`
        .speeddial-fixed {
          position: fixed;
          right: 1rem;
          bottom: 1rem;
          z-index: 1050;
        }
      `}</style>
      {SpeedDialFab}
    </div>
  );
}
