// src/pages/CategoryCatalog.js
import React, { useEffect, useMemo, useRef, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Toolbar } from "primereact/toolbar";
import { Dropdown } from "primereact/dropdown";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Tag } from "primereact/tag";
import { Button } from "primereact/button";

import { createApi, getAxiosMessage } from "../lib/api";
import { previewUrl } from "../lib/img";

const THUMB = { width: 64, height: 64, objectFit: "cover", borderRadius: 6 };

export default function CategoryCatalogPage({ baseUrl, onToast }) {
  const api = useMemo(() => createApi(baseUrl), [baseUrl]);

  const [q, setQ] = useState("");
  const [filterCatId, setFilterCatId] = useState(null);
  const [minPrice, setMinPrice] = useState(null);
  const [maxPrice, setMaxPrice] = useState(null);

  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [categories, setCategories] = useState([]);
  const catMap = useMemo(() => {
    const m = new Map();
    categories.forEach((c) => m.set(c.id, c));
    return m;
  }, [categories]);

  const dt = useRef(null);

  const catOptions = useMemo(
    () => [
      { label: "(Tất cả)", value: null },
      ...categories.map((c) => ({ label: `${c.name} (#${c.id})`, value: c.id })),
    ],
    [categories]
  );

  const loadCategories = async () => {
    try {
      const { data } = await api.get("/api/categories", { params: { page: 1, size: 999 } });
      setCategories(data?.items || []);
    } catch (err) {
      onToast("warn", "Không tải được danh mục", getAxiosMessage(err));
    }
  };

  const loadingData = async () => {
    setLoading(true);
    try {
      const { data } = await api.get("/api/catalogs", {
        params: { page, size, q, categoryId: filterCatId, minPrice, maxPrice },
      });
      setRows(data?.items || []);
      setTotal(data?.total || 0);
    } catch (err) {
      onToast("error", "Tải dữ liệu thất bại", getAxiosMessage(err));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { loadingData(); /* eslint-disable-next-line */ }, [page, size]);

  const onSearch = () => { setPage(1); loadingData(); };
  const onClear = () => { setQ(""); setFilterCatId(null); setMinPrice(null); setMaxPrice(null); setPage(1); loadingData(); };

  const Header = (
    <Toolbar
      left={() => (
        <div className="flex align-items-center gap-2">
          <span className="p-input-icon-left">
            <i className="pi pi-search" />
            <InputText
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Tìm kiếm (name/slug/code)"
              onKeyDown={(e) => e.key === "Enter" && onSearch()}
              style={{ minWidth: 260 }}
            />
          </span>

          <Dropdown
            value={filterCatId}
            options={catOptions}
            onChange={(e) => setFilterCatId(e.value)}
            placeholder="Danh mục"
            showClear
            style={{ minWidth: 220 }}
          />

          <span>
            Giá từ{" "}
            <InputNumber
              value={minPrice}
              onValueChange={(e) => setMinPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
            />{" "}
            đến{" "}
            <InputNumber
              value={maxPrice}
              onValueChange={(e) => setMaxPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
            />
          </span>

          <Button icon="pi pi-filter" label="Lọc" onClick={onSearch} />
          <Button icon="pi pi-times" className="p-button-text" label="Xoá lọc" onClick={onClear} />
        </div>
      )}
      right={() => (
        <div>
          <Button icon="pi pi-download" label="Export CSV" onClick={() => dt.current?.exportCSV()} />
        </div>
      )}
    />
  );

  return (
    <div>
      {Header}

      <DataTable
        ref={dt}
        value={rows}
        loading={loading}
        paginator
        lazy
        rows={size}
        totalRecords={total}
        first={(page - 1) * size}
        onPage={(e) => {
          setPage(Math.floor(e.first / e.rows) + 1);
          setSize(e.rows);
        }}
        rowsPerPageOptions={[5, 10, 20, 50]}
        emptyMessage="Không có dữ liệu"
      >
        <Column field="id" header="#ID" style={{ width: 80 }} />

        <Column
          header="Ảnh"
          body={(r) =>
            r.imageUrl ? (
              <img src={previewUrl(r.imageUrl)} alt={r.name} style={THUMB} />
            ) : (
              <span className="pi pi-image" />
            )
          }
          style={{ width: 90 }}
        />

        <Column field="name" header="Catalog Name" />
        <Column field="code" header="Code" style={{ width: 140 }} />
        <Column
          field="price"
          header="Price"
          body={(r) => r.price?.toLocaleString(undefined, { style: "currency", currency: "USD" })}
          style={{ width: 150 }}
        />
        <Column header="Category" body={(r) => catMap.get(r.categoryId)?.name || r.categoryId} style={{ width: 220 }} />
        {/* ĐÃ BỎ: Category Id & Category Parent */}
        <Column
          header="Active"
          body={(r) => (
            <Tag value={r.isActive ? "Active" : "Inactive"} severity={r.isActive ? "success" : "secondary"} />
          )}
          style={{ width: 140 }}
        />
      </DataTable>
    </div>
  );
}
