import React, { useEffect, useMemo, useRef, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Dropdown } from "primereact/dropdown";
import { InputSwitch } from "primereact/inputswitch";
import { Button } from "primereact/button";
import { ConfirmDialog, confirmDialog } from "primereact/confirmdialog";
import { Tag } from "primereact/tag";
import { Panel } from "primereact/panel";
import { Image } from "primereact/image";
import { Toolbar } from "primereact/toolbar";
import { Chip } from "primereact/chip";
import { Divider } from "primereact/divider";
import { InputTextarea } from "primereact/inputtextarea";
import { FloatLabel } from "primereact/floatlabel";
import { Card } from "primereact/card";

import { http, buildQuery } from "../lib/api";

export default function CatalogsPage({ baseUrl, onToast }) {
  // State
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [categories, setCategories] = useState([]);
  const [filterCatId, setFilterCatId] = useState(null);
  const [minPrice, setMinPrice] = useState(null);
  const [maxPrice, setMaxPrice] = useState(null);

  const [dialogOpen, setDialogOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    name: "",
    code: "",
    categoryId: 0,
    price: 0,
    imageUrl: "",
    description: "",
    isActive: true,
  });

  const dt = useRef(null);

  // Helpers
  const catOptions = useMemo(
    () => categories.map((c) => ({ label: `${c.name} (#${c.id})`, value: c.id })),
    [categories]
  );
  const catName = (id) => categories.find((c) => c.id === id)?.name || id;

  const hasAnyFilter = !!(q || filterCatId !== null || minPrice != null || maxPrice != null);

  const clearFilter = (key) => {
    if (key === "q") setQ("");
    if (key === "cat") setFilterCatId(null);
    if (key === "min") setMinPrice(null);
    if (key === "max") setMaxPrice(null);
    setPage(1);
    setTimeout(fetchData, 0);
  };

  // API
  const loadCategories = async () => {
    try {
      const data = await http(baseUrl, "/api/categories" + buildQuery({ page: 1, size: 999 }));
      setCategories(data.items || []);
    } catch (err) {
      onToast("warn", "Không tải được danh mục", err.message);
    }
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await http(
        baseUrl,
        "/api/catalogs" + buildQuery({ page, size, q, categoryId: filterCatId, minPrice, maxPrice })
      );
      setRows(data.items || []);
      setTotal(data.total || 0);
    } catch (err) {
      onToast("error", "Tải catalogs thất bại", err.message);
    } finally {
      setLoading(false);
    }
  };

  // Effects
  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [page, size]);

  // Actions
  const onSearch = () => { setPage(1); fetchData(); };

  const openNew = () => {
    setEditId(null);
    setForm({
      name: "",
      code: "",
      categoryId: categories[0]?.id ?? 0,
      price: 0,
      imageUrl: "",
      description: "",
      isActive: true,
    });
    setDialogOpen(true);
  };

  const openEdit = (r) => {
    setEditId(r.id);
    setForm({
      name: r.name,
      code: r.code,
      categoryId: r.categoryId,
      price: r.price,
      imageUrl: r.imageUrl || "",
      description: r.description || "",
      isActive: !!r.isActive,
    });
    setDialogOpen(true);
  };

  const save = async () => {
    if (!form.name?.trim() || !form.code?.trim()) {
      return onToast("warn", "Thiếu dữ liệu", "Tên và Code bắt buộc");
    }
    if (!form.categoryId) {
      return onToast("warn", "Thiếu dữ liệu", "Chọn Category");
    }

    try {
      const body = JSON.stringify({ ...form, price: Number(form.price) || 0 });
      if (editId == null) {
        await http(baseUrl, "/api/catalogs", { method: "POST", body });
        onToast("success", "Đã tạo", form.name);
      } else {
        await http(baseUrl, `/api/catalogs/${editId}`, { method: "PUT", body });
        onToast("success", "Đã cập nhật", form.name);
      }
      setDialogOpen(false);
      fetchData();
    } catch (err) {
      onToast("error", "Lưu thất bại", err.message);
    }
  };

  const remove = (r) =>
    confirmDialog({
      message: `Xoá sản phẩm "${r.name}"?`,
      header: "Xác nhận",
      icon: "pi pi-exclamation-triangle",
      acceptClassName: "p-button-danger",
      accept: async () => {
        try {
          await http(baseUrl, `/api/catalogs/${r.id}`, { method: "DELETE" });
          onToast("success", "Đã xoá", r.name);
          fetchData();
        } catch (err) {
          onToast("error", "Xoá thất bại", err.message);
        }
      },
    });

  const toggleActive = async (r) => {
    try {
      const body = JSON.stringify({
        name: r.name,
        code: r.code,
        categoryId: r.categoryId,
        price: r.price,
        imageUrl: r.imageUrl || "",
        description: r.description || "",
        isActive: !r.isActive,
      });
      await http(baseUrl, `/api/catalogs/${r.id}`, { method: "PUT", body });
      onToast("success", !r.isActive ? "Đã kích hoạt" : "Đã tắt", r.name);
      fetchData();
    } catch (err) {
      onToast("error", "Đổi trạng thái thất bại", err.message);
    }
  };

  // Header toolbar (mặc định, không style)
  const HeaderToolbar = (
    <Toolbar
      left={() => (
        <div>
          <span className="p-input-icon-left" style={{ marginRight: 8 }}>
            <i className="pi pi-search" />
            <InputText
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Tìm theo tên/slug/code"
              onKeyDown={(e) => e.key === "Enter" && onSearch()}
            />
          </span>
          <Dropdown
            value={filterCatId}
            options={[{ label: "(Tất cả)", value: null }, ...catOptions]}
            onChange={(e) => setFilterCatId(e.value)}
            placeholder="Danh mục"
            showClear
            style={{ marginRight: 8 }}
          />
          <span style={{ marginRight: 8 }}>
            Giá từ{" "}
            <InputNumber
              value={minPrice}
              onValueChange={(e) => setMinPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
            />{" "}
            đến{" "}
            <InputNumber
              value={maxPrice}
              onValueChange={(e) => setMaxPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
            />
          </span>
          <Button icon="pi pi-filter" label="Lọc" onClick={onSearch} style={{ marginRight: 8 }} />
          <Button icon="pi pi-refresh" className="p-button-text" onClick={fetchData} />
        </div>
      )}
      right={() => (
        <div>
          <Button
            icon="pi pi-download"
            label="Export CSV"
            onClick={() => dt.current?.exportCSV()}
            style={{ marginRight: 8 }}
          />
          <Button icon="pi pi-plus" label="Thêm" onClick={openNew} />
        </div>
      )}
    />
  );

  const ActiveFilterChips = hasAnyFilter && (
    <div>
      {q && <Chip label={`Tìm: ${q}`} onRemove={() => clearFilter("q")} removable style={{ marginRight: 8 }} />}
      {filterCatId !== null && (
        <Chip
          label={`Danh mục: ${catName(filterCatId)}`}
          onRemove={() => clearFilter("cat")}
          removable
          style={{ marginRight: 8 }}
        />
      )}
      {minPrice != null && (
        <Chip label={`Min: ${minPrice}`} onRemove={() => clearFilter("min")} removable style={{ marginRight: 8 }} />
      )}
      {maxPrice != null && <Chip label={`Max: ${maxPrice}`} onRemove={() => clearFilter("max")} removable />}
      <Button
        className="p-button-text"
        onClick={() => {
          setQ("");
          setFilterCatId(null);
          setMinPrice(null);
          setMaxPrice(null);
          setPage(1);
          fetchData();
        }}
        style={{ marginLeft: 8 }}
      >
        Xoá tất cả
      </Button>
    </div>
  );

  // Render
  return (
    <div>
      <ConfirmDialog />

      {HeaderToolbar}
      {ActiveFilterChips}

      <Panel header="Danh sách sản phẩm">
        <DataTable
          ref={dt}
          value={rows}
          loading={loading}
          paginator
          rows={size}
          totalRecords={total}
          first={(page - 1) * size}
          onPage={(e) => {
            setPage(Math.floor(e.first / e.rows) + 1);
            setSize(e.rows);
          }}
          rowsPerPageOptions={[5, 10, 20, 50]}
          emptyMessage="Không có dữ liệu"
        >
          <Column field="id" header="#" />

          <Column
            header="Ảnh"
            body={(r) =>
              r.imageUrl ? (
                <Image src={r.imageUrl} alt={r.name} preview />
              ) : (
                <span className="pi pi-image" />
              )
            }
          />

          <Column
            field="name"
            header="Tên"
            sortable
            body={(r) => (
              <div>
                <div>{r.name}</div>
                {r.description && <small title={r.description}>{r.description}</small>}
              </div>
            )}
          />
          <Column field="code" header="Code" sortable />
          <Column header="Danh mục" body={(r) => catName(r.categoryId)} />
          <Column
            field="price"
            header="Giá"
            body={(r) => r.price?.toLocaleString(undefined, { style: "currency", currency: "USD" })}
          />
          <Column
            header="Trạng thái"
            body={(r) => (
              <Tag
                value={r.isActive ? "Active" : "Inactive"}
                severity={r.isActive ? "success" : "secondary"}
                onClick={() => toggleActive(r)}
              />
            )}
          />
          <Column
            header="Thao tác"
            body={(r) => (
              <span>
                <Button icon="pi pi-pencil" className="p-button-text" onClick={() => openEdit(r)} />
                <Button
                  icon="pi pi-trash"
                  className="p-button-text p-button-danger"
                  onClick={() => remove(r)}
                />
              </span>
            )}
          />
        </DataTable>
      </Panel>

      <Dialog
        visible={dialogOpen}
        onHide={() => setDialogOpen(false)}
        modal
        header={editId == null ? "Thêm sản phẩm" : "Sửa sản phẩm"}
        style={{ width: "60rem", maxWidth: "95vw" }}   // rộng hơn chút cho 2 cột
        footer={
          <div className="flex gap-2 justify-content-end">
            <Button
              label="Huỷ"
              className="p-button-text"
              onClick={() => setDialogOpen(false)}
              icon="pi pi-times"
            />
            <Button
              label={editId == null ? "Tạo mới" : "Lưu thay đổi"}
              icon="pi pi-save"
              onClick={handleSave}
              disabled={invalid}
              loading={saving}
            />
          </div>
        }
      >
        <div className="p-fluid grid formgrid">
          {/* ===== Thông tin cơ bản ===== */}
          <div className="col-12">
            <Divider align="left">
              <span className="flex align-items-center gap-2">
                <i className="pi pi-info-circle" />
                <span>Thông tin cơ bản</span>
              </span>
            </Divider>
          </div>

          <div className="field col-12 md:col-8">
            <FloatLabel>
              <InputText
                id="name"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                onKeyDown={(e) => e.key === "Enter" && handleSave()}
              />
              <label htmlFor="name">Tên *</label>
            </FloatLabel>
            {!form.name?.trim() && <small className="p-error">Bắt buộc</small>}
          </div>

          <div className="field col-12 md:col-4">
            <FloatLabel>
              <InputText
                id="code"
                value={form.code}
                onChange={(e) => setForm({ ...form, code: e.target.value })}
                onKeyDown={(e) => e.key === "Enter" && handleSave()}
              />
              <label htmlFor="code">Code *</label>
            </FloatLabel>
            {!form.code?.trim() && <small className="p-error">Bắt buộc</small>}
          </div>

          <div className="field col-12 md:col-6">
            <FloatLabel>
              <Dropdown
                id="categoryId"
                inputId="categoryId"
                value={form.categoryId}
                options={catOptions}
                onChange={(e) => setForm({ ...form, categoryId: e.value })}
                className="w-full"
              />
              <label htmlFor="categoryId">Danh mục *</label>
            </FloatLabel>
            {!form.categoryId && <small className="p-error">Bắt buộc</small>}
          </div>

          <div className="field col-12 md:col-6">
            <label className="block mb-1">Giá</label>
            <InputNumber
              value={form.price}
              onValueChange={(e) => setForm({ ...form, price: e.value ?? 0 })}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-full"
            />
          </div>

          <div className="field col-12 md:col-6">
            <label className="block mb-2">Kích hoạt</label>
            <div className="flex align-items-center gap-2">
              <InputSwitch
                checked={form.isActive}
                onChange={(e) => setForm({ ...form, isActive: e.value })}
              />
              <Tag
                value={form.isActive ? "Active" : "Inactive"}
                severity={form.isActive ? "success" : "secondary"}
              />
            </div>
          </div>

          {/* ===== Ảnh & mô tả ===== */}
          <div className="col-12">
            <Divider align="left">
              <span className="flex align-items-center gap-2">
                <i className="pi pi-image" />
                <span>Hình ảnh & mô tả</span>
              </span>
            </Divider>
          </div>

          <div className="field col-12 md:col-8">
            <FloatLabel>
              <InputText
                id="imageUrl"
                value={form.imageUrl}
                onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
                placeholder="https://..."
              />
              <label htmlFor="imageUrl">Ảnh (URL)</label>
            </FloatLabel>
            <small className="p-text-secondary">
              Dán URL ảnh; để trống nếu chưa có.
            </small>
          </div>

          <div className="field col-12 md:col-4">
            {/* Preview gọn, dùng Image có preview */}
            {form.imageUrl ? (
              <Image
                src={form.imageUrl}
                alt="preview"
                imageStyle={{ width: "100%", maxHeight: 220, objectFit: "cover" }}
                preview
              />
            ) : (
              <div className="flex align-items-center justify-content-center border-1 surface-border" style={{ height: 220 }}>
                <span className="pi pi-image p-2" />
              </div>
            )}
          </div>

          <div className="field col-12">
            <FloatLabel>
              <InputTextarea
                id="description"
                value={form.description}
                onChange={(e) => setForm({ ...form, description: e.target.value })}
                autoResize
                rows={3}
              />
              <label htmlFor="description">Mô tả</label>
            </FloatLabel>
          </div>

          {/* ===== Tóm tắt nhanh ===== */}
          <div className="col-12">
            <Divider align="left">
              <span className="flex align-items-center gap-2">
                <i className="pi pi-eye" />
                <span>Preview</span>
              </span>
            </Divider>
          </div>

          <div className="col-12 md:col-8">
            <div className="flex flex-column gap-1">
              <strong>{form.name || "Tên sản phẩm"}</strong>
              <span className="p-text-secondary">{form.code || "—"}</span>
              <span>
                {(catOptions.find((c) => c.value === form.categoryId)?.label) || "Danh mục"}
              </span>
              <span>
                {(Number(form.price) || 0).toLocaleString(undefined, { style: "currency", currency: "USD" })}
              </span>
            </div>
          </div>
        </div>
      </Dialog>

    </div>
  );
}
