import React, { useEffect, useMemo, useRef, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Toolbar } from "primereact/toolbar";
import { Dropdown } from "primereact/dropdown";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Tag } from "primereact/tag";
import { Button } from "primereact/button";
import { http, buildQuery, normalizeBase } from "../lib/api";
import axios from "axios";

export default function CategoryCatalogPage({ baseUrl, onToast }) {
  const [q, setQ] = useState("");
  const [filterCatId, setFilterCatId] = useState(null);
  const [minPrice, setMinPrice] = useState(null);
  const [maxPrice, setMaxPrice] = useState(null);

  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [categories, setCategories] = useState([]);

  const api = useMemo(() => {
    const base = normalizeBase(baseUrl)
    return axios.create({
      baseURL: baseUrl?.replace(/\/+$/, "") || "",
      timeout: 15000,
      withCredentials: false,
    });
  }, [baseUrl]);

  const catMap = useMemo(() => {
    const m = new Map();
    categories.forEach(c => m.set(c.id, c));
    return m;
  }, [categories]);

  const dt = useRef(null);

  const catOptions = useMemo(
    () => [{ label: "(Tất cả)", value: null }, ...categories.map(c => ({ label: `${c.name} (#${c.id})`, value: c.id }))],
    [categories]
  );

  const loadCategories = async () => {
    try {
      const data = await api.get(baseUrl, "/api/categories" + {param:{ page: 1, size: 999 }});
      setCategories(data.items || []);
    } catch (err) {
      onToast("warn", "Không tải được danh mục", err.message);
    }
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await api.get(
        baseUrl,
        "/api/catalogs" + {params: { page, size, q, categoryId: filterCatId, minPrice, maxPrice }}
      );
      setRows(data.items || []);
      setTotal(data.total || 0);
    } catch (err) {
      onToast("error", "Tải dữ liệu thất bại", err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [page, size]);

  const onSearch = () => { setPage(1); fetchData(); };

  const Header = (
    <Toolbar
      left={() => (
        <div>
          <span className="p-input-icon-left" style={{ marginRight: 8 }}>
            <InputText
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Tìm theo tên/slug/code"
              onKeyDown={(e) => e.key === "Enter" && onSearch()}
            />
          </span>
          <Dropdown
            value={filterCatId}
            options={catOptions}
            onChange={(e) => setFilterCatId(e.value)}
            placeholder="Danh mục"
            showClear
            style={{ marginRight: 8 }}
          />
          <span style={{ marginRight: 8 }}>
            Giá từ{" "}
            <InputNumber
              value={minPrice}
              onValueChange={(e) => setMinPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
            />{" "}
            đến{" "}
            <InputNumber
              value={maxPrice}
              onValueChange={(e) => setMaxPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
            />
          </span>
          <Button icon="pi pi-filter" label="Lọc" onClick={onSearch} style={{ marginRight: 8 }} />
          <Button icon="pi pi-refresh" className="p-button-text" onClick={fetchData} />
        </div>
      )}
      right={() => (
        <div>
          <Button icon="pi pi-download" label="Export CSV" onClick={() => dt.current?.exportCSV()} />
        </div>
      )}
    />
  );

  return (
    <div>
      {Header}

      <DataTable
        ref={dt}
        value={rows}
        loading={loading}
        paginator
        lazy
        rows={size}
        totalRecords={total}
        first={(page - 1) * size}
        onPage={(e) => { setPage(Math.floor(e.first / e.rows) + 1); setSize(e.rows); }}
        rowsPerPageOptions={[5, 10, 20, 50]}
        emptyMessage="Không có dữ liệu"
      >
        {/* CATALOG FIELDS */}
        <Column field="id" header="#ID" />
        <Column field="name" header="Catalog Name" sortable />
        <Column field="code" header="Code" sortable />
        <Column field="price" header="Price" body={(r) => r.price?.toLocaleString(undefined, { style: "currency", currency: "USD" })} />

        {/* CATEGORY JOINED FIELDS */}
        <Column header="Category" body={(r) => catMap.get(r.categoryId)?.name || r.categoryId} />
        <Column header="Category Id" body={(r) => r.categoryId} />
        <Column header="Category Parent" body={(r) => catMap.get(r.categoryId)?.parentId ?? ""} />

        {/* STATUS */}
        <Column header="Active" body={(r) => (
          <Tag value={r.isActive ? "Active" : "Inactive"} severity={r.isActive ? "success" : "secondary"} />
        )} />
      </DataTable>
    </div>
  );
}
