import React from "react";
import { Toolbar } from "primereact/toolbar";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
import { testConnection } from "../lib/api";


export default function TopBar({ baseUrl, setBaseUrl, onToast }) {
    return (
    <Toolbar
        className="mb-3 shadow-1 surface-card border-round-xl"
        left={() => (
            <div className="flex gap-2 align-items-center">
            <i className="pi pi-globe text-primary text-xl" />
            <span className="font-semibold">CategoryApi</span>
            </div>
        )}
        right={() => (
        <div className="flex align-items-center gap-2">
        <span className="p-input-icon-left">
        <i className="pi pi-link" />
        <InputText value={baseUrl} onChange={(e) => setBaseUrl(e.target.value)} placeholder="Base URL" className="w-20rem" />
        </span>
        <Button
            icon="pi pi-check"
            label="Test"
            onClick={async () => {
            try {
                await testConnection(baseUrl);
                onToast("success", "Kết nối OK", baseUrl);
            } catch (err) {
                onToast("error", "Kết nối lỗi", err.message);
            }
            }}
        />
        </div>
        )}
    />
    );
}
