import React, { useEffect, useMemo, useRef, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { Dropdown } from "primereact/dropdown";
import { InputSwitch } from "primereact/inputswitch";
import { Button } from "primereact/button";
import { ConfirmDialog, confirmDialog } from "primereact/confirmdialog";
import { Tag } from "primereact/tag";
import { Panel } from "primereact/panel";
import { Image } from "primereact/image";
import { Toolbar } from "primereact/toolbar";
import { Avatar } from "primereact/avatar";
import { Chip } from "primereact/chip";
import { SpeedDial } from "primereact/speeddial";
import { http, buildQuery } from "../lib/api";

export default function CatalogsPage({ baseUrl, onToast }) {
  // ------------------ State ------------------
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [categories, setCategories] = useState([]);
  const [filterCatId, setFilterCatId] = useState(null);
  const [minPrice, setMinPrice] = useState(null);
  const [maxPrice, setMaxPrice] = useState(null);

  const [dialogOpen, setDialogOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({
    name: "",
    code: "",
    categoryId: 0,
    price: 0,
    imageUrl: "",
    description: "",
    isActive: true,
  });

  // refs
  const dt = useRef(null);

  // ------------------ Memos/helpers ------------------
  const catOptions = useMemo(
    () => categories.map((c) => ({ label: `${c.name} (#${c.id})`, value: c.id })),
    [categories]
  );
  const catName = (id) => categories.find((c) => c.id === id)?.name || id;

  const hasAnyFilter = !!(q || filterCatId !== null || minPrice != null || maxPrice != null);

  const clearFilter = (key) => {
    switch (key) {
      case "q": setQ(""); break;
      case "cat": setFilterCatId(null); break;
      case "min": setMinPrice(null); break;
      case "max": setMaxPrice(null); break;
      default: break;
    }
    setPage(1);
    // fetch sau một tick để state kịp cập nhật
    setTimeout(fetchData, 0);
  };

  // ------------------ API calls ------------------
  const loadCategories = async () => {
    try {
      const data = await http(baseUrl, "/api/categories" + buildQuery({ page: 1, size: 999 }));
      setCategories(data.items || []);
    } catch (err) {
      onToast("warn", "Không tải được danh mục", err.message);
    }
  };

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await http(
        baseUrl,
        "/api/catalogs" +
          buildQuery({ page, size, q, categoryId: filterCatId, minPrice, maxPrice })
      );
      setRows(data.items || []);
      setTotal(data.total || 0);
    } catch (err) {
      onToast("error", "Tải catalogs thất bại", err.message);
    } finally {
      setLoading(false);
    }
  };

  // ------------------ Effects ------------------
  useEffect(() => { loadCategories(); }, []);
  useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [page, size]);

  // ------------------ Actions ------------------
  const onSearch = () => { setPage(1); fetchData(); };

  const openNew = () => {
    setEditId(null);
    setForm({
      name: "",
      code: "",
      categoryId: categories[0]?.id ?? 0,
      price: 0,
      imageUrl: "",
      description: "",
      isActive: true,
    });
    setDialogOpen(true);
  };

  const openEdit = (r) => {
    setEditId(r.id);
    setForm({
      name: r.name,
      code: r.code,
      categoryId: r.categoryId,
      price: r.price,
      imageUrl: r.imageUrl || "",
      description: r.description || "",
      isActive: !!r.isActive,
    });
    setDialogOpen(true);
  };

  const save = async () => {
    if (!form.name?.trim() || !form.code?.trim()) {
      return onToast("warn", "Thiếu dữ liệu", "Tên và Code bắt buộc");
    }
    if (!form.categoryId) {
      return onToast("warn", "Thiếu dữ liệu", "Chọn Category");
    }

    try {
      const body = JSON.stringify({ ...form, price: Number(form.price) || 0 });
      if (editId == null) {
        await http(baseUrl, "/api/catalogs", { method: "POST", body });
        onToast("success", "Đã tạo", form.name);
      } else {
        await http(baseUrl, `/api/catalogs/${editId}`, { method: "PUT", body });
        onToast("success", "Đã cập nhật", form.name);
      }
      setDialogOpen(false);
      fetchData();
    } catch (err) {
      onToast("error", "Lưu thất bại", err.message);
    }
  };

  const remove = (r) =>
    confirmDialog({
      message: `Xoá sản phẩm "${r.name}"?`,
      header: "Xác nhận",
      icon: "pi pi-exclamation-triangle",
      acceptClassName: "p-button-danger",
      accept: async () => {
        try {
          await http(baseUrl, `/api/catalogs/${r.id}`, { method: "DELETE" });
          onToast("success", "Đã xoá", r.name);
          fetchData();
        } catch (err) {
          onToast("error", "Xoá thất bại", err.message);
        }
      },
    });

  const toggleActive = async (r) => {
    try {
      const body = JSON.stringify({
        name: r.name,
        code: r.code,
        categoryId: r.categoryId,
        price: r.price,
        imageUrl: r.imageUrl || "",
        description: r.description || "",
        isActive: !r.isActive
      });
      await http(baseUrl, `/api/catalogs/${r.id}`, { method: "PUT", body });
      onToast("success", !r.isActive ? "Đã kích hoạt" : "Đã tắt", r.name);
      fetchData();
    } catch (err) {
      onToast("error", "Đổi trạng thái thất bại", err.message);
    }
  };

  // ------------------ UI pieces ------------------
  const HeaderToolbar = (
    <Toolbar
      className="surface-card border-round-2xl shadow-2 mb-3 px-3 py-2"
      left={() => (
        <div className="flex flex-wrap gap-2 align-items-center">
          <span className="p-input-icon-left">
            <i className="pi pi-search" />
            <InputText
              value={q}
              onChange={(e) => setQ(e.target.value)}
              placeholder="Tìm theo tên/slug/code"
              onKeyDown={(e) => e.key === "Enter" && onSearch()}
              className="w-18rem"
            />
          </span>
          <Dropdown
            value={filterCatId}
            options={[{ label: "(Tất cả)", value: null }, ...catOptions]}
            onChange={(e) => setFilterCatId(e.value)}
            placeholder="Danh mục"
            className="w-15rem"
            showClear
          />
          <span className="flex align-items-center gap-2">
            <span className="text-600">Giá từ</span>
            <InputNumber
              value={minPrice}
              onValueChange={(e) => setMinPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-12rem"
            />
            <span className="text-600">đến</span>
            <InputNumber
              value={maxPrice}
              onValueChange={(e) => setMaxPrice(e.value)}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-12rem"
            />
          </span>
          <Button icon="pi pi-filter" label="Lọc" onClick={onSearch} />
          <Button icon="pi pi-refresh" className="p-button-text" onClick={fetchData} />
        </div>
      )}
      right={() => (
        <div className="flex gap-2">
          <Button icon="pi pi-download" label="Export CSV" outlined onClick={() => dt.current?.exportCSV()} />
          <Button icon="pi pi-plus" label="Thêm" onClick={openNew} />
        </div>
      )}
    />
  );

  const ActiveFilterChips = hasAnyFilter && (
    <div className="flex flex-wrap gap-2 mb-2">
      {q && <Chip label={`Tìm: ${q}`} icon="pi pi-times" onIconClick={() => clearFilter("q")} removable />}
      {filterCatId !== null && (
        <Chip label={`Danh mục: ${catName(filterCatId)}`} icon="pi pi-times" onIconClick={() => clearFilter("cat")} removable />
      )}
      {minPrice != null && <Chip label={`Min: ${minPrice}`} icon="pi pi-times" onIconClick={() => clearFilter("min")} removable />}
      {maxPrice != null && <Chip label={`Max: ${maxPrice}`} icon="pi pi-times" onIconClick={() => clearFilter("max")} removable />}
      <Button size="small" className="p-button-text" onClick={() => { setQ(""); setFilterCatId(null); setMinPrice(null); setMaxPrice(null); setPage(1); fetchData(); }}>
        Xoá tất cả
      </Button>
    </div>
  );

  const SpeedDialFab = (
    <SpeedDial
      className="speeddial-fixed"
      radius={60}
      type="quarter-circle"
      direction="up-left"
      buttonClassName="p-button-rounded p-button-lg"
      model={[
        { label: "Thêm", icon: "pi pi-plus", command: openNew },
        { label: "Refresh", icon: "pi pi-refresh", command: fetchData },
        { label: "Export CSV", icon: "pi pi-download", command: () => dt.current?.exportCSV() },
      ]}
    />
  );

  // ------------------ Render ------------------
  return (
    <div className="flex flex-column gap-3">
      <ConfirmDialog />

      {HeaderToolbar}
      {ActiveFilterChips}

      <Panel header="Danh sách sản phẩm" className="surface-card border-round-2xl shadow-2">
        <DataTable
          ref={dt}
          value={rows}
          loading={loading}
          paginator
          rows={size}
          totalRecords={total}
          first={(page - 1) * size}
          onPage={(e) => { setPage(Math.floor(e.first / e.rows) + 1); setSize(e.rows); }}
          rowsPerPageOptions={[5, 10, 20, 50]}
          paginatorTemplate="RowsPerPageDropdown FirstPageLink PrevPageLink CurrentPageReport NextPageLink LastPageLink"
          currentPageReportTemplate="({first}–{last} / {totalRecords})"
          emptyMessage={
            <div className="text-center p-4">
              <i className="pi pi-box text-400 text-3xl mb-2" />
              <div className="text-600">Chưa có dữ liệu. Nhấn <b>Thêm</b> để tạo mới.</div>
            </div>
          }
          showGridlines
          rowHover
          stripedRows
          responsiveLayout="scroll"
          className="border-round-2xl"
        >
          <Column field="id" header="#" style={{ width: 70 }} />

          <Column
            header="Ảnh"
            style={{ width: 100 }}
            body={(r) =>
              r.imageUrl ? (
                <Image
                  src={r.imageUrl}
                  alt={r.name}
                  imageStyle={{ width: 44, height: 44, objectFit: "cover", borderRadius: 8 }}
                  preview
                />
              ) : (
                <Avatar icon="pi pi-image" shape="square" className="bg-gray-100 text-600" style={{ width: 44, height: 44 }} />
              )
            }
          />

          <Column field="name" header="Tên" sortable body={(r) => (
            <div className="flex flex-column">
              <span className="font-medium">{r.name}</span>
              {r.description && <small className="text-600" title={r.description}>{r.description.slice(0, 64)}{r.description.length > 64 ? "…" : ""}</small>}
            </div>
          )} />
          <Column field="code" header="Code" sortable style={{ width: 160 }} />
          <Column header="Danh mục" body={(r) => <Tag value={catName(r.categoryId)} severity="info" rounded />} />
          <Column
            field="price"
            header="Giá"
            body={(r) => r.price?.toLocaleString(undefined, { style: "currency", currency: "USD" })}
            style={{ width: 160, textAlign: "right" }}
          />
          <Column
            header="Trạng thái"
            style={{ width: 160 }}
            body={(r) => (
              <Tag
                value={r.isActive ? "Active" : "Inactive"}
                severity={r.isActive ? "success" : "secondary"}
                rounded
                className="cursor-pointer"
                onClick={() => toggleActive(r)}
                title="Nhấn để đổi trạng thái"
              />
            )}
          />
          <Column
            header="Thao tác"
            style={{ width: 180 }}
            body={(r) => (
              <div className="flex gap-1 justify-content-end">
                <Button
                  icon="pi pi-pencil"
                  className="p-button-rounded p-button-text p-button-sm"
                  onClick={() => openEdit(r)}
                  tooltip="Sửa"
                />
                <Button
                  icon="pi pi-trash"
                  className="p-button-rounded p-button-text p-button-danger p-button-sm"
                  onClick={() => remove(r)}
                  tooltip="Xoá"
                />
              </div>
            )}
          />
        </DataTable>
      </Panel>

      {/* Speed actions */}
      {SpeedDialFab}

      {/* Dialog create/edit */}
      <Dialog
        header={editId == null ? "Thêm sản phẩm" : "Sửa sản phẩm"}
        visible={dialogOpen}
        style={{ width: "42rem" }}
        modal
        onHide={() => setDialogOpen(false)}
        className="border-round-2xl"
      >
        <div className="grid gap-3">
          <div className="col-12">
            <label className="block mb-1 font-medium">Tên *</label>
            <InputText value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
          </div>

          <div className="col-6">
            <label className="block mb-1 font-medium">Code *</label>
            <InputText value={form.code} onChange={(e) => setForm({ ...form, code: e.target.value })} />
          </div>

          <div className="col-6">
            <label className="block mb-1 font-medium">Danh mục *</label>
            <Dropdown value={form.categoryId} options={catOptions} onChange={(e) => setForm({ ...form, categoryId: e.value })} className="w-full" />
          </div>

          <div className="col-6">
            <label className="block mb-1 font-medium">Giá</label>
            <InputNumber
              value={form.price}
              onValueChange={(e) => setForm({ ...form, price: e.value ?? 0 })}
              mode="currency"
              currency="USD"
              locale="en-US"
              className="w-full"
            />
          </div>

          <div className="col-6 flex align-items-center gap-2">
            <label className="font-medium">Kích hoạt</label>
            <InputSwitch checked={form.isActive} onChange={(e) => setForm({ ...form, isActive: e.value })} />
          </div>

          <div className="col-12">
            <label className="block mb-1 font-medium">Ảnh (URL)</label>
            <InputText value={form.imageUrl} onChange={(e) => setForm({ ...form, imageUrl: e.target.value })} placeholder="https://..." />
          </div>

          <div className="col-12">
            <label className="block mb-1 font-medium">Mô tả</label>
            <InputText value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />
          </div>
        </div>

        <div className="flex justify-content-end gap-2 mt-4">
          <Button label="Huỷ" className="p-button-text" onClick={() => setDialogOpen(false)} />
          <Button label="Lưu" icon="pi pi-save" onClick={save} />
        </div>
      </Dialog>

      {/* SpeedDial vị trí cố định nếu bạn chưa có CSS riêng */}
      <style>{`
        .speeddial-fixed { position: fixed; right: 1.25rem; bottom: 1.25rem; z-index: 10; }
      `}</style>
    </div>
  );
}
