import React, { useEffect, useState } from "react";
import { DataTable } from "primereact/datatable";
import { Column } from "primereact/column";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";
import { InputSwitch } from "primereact/inputswitch";
import { Button } from "primereact/button";
import { ConfirmDialog, confirmDialog } from "primereact/confirmdialog";
import { http, buildQuery } from "../lib/api";

export default function CategoriesPage({ baseUrl, onToast }) {
  const [q, setQ] = useState("");
  const [page, setPage] = useState(1);
  const [size, setSize] = useState(10);
  const [total, setTotal] = useState(0);
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const [dialogOpen, setDialogOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ name: "", parentId: null, sortOrder: 0, isActive: true });

  const fetchData = async () => {
    setLoading(true);
    try {
      const data = await http(baseUrl, "/api/categories" + buildQuery({ page, size, q }));
      setRows(data.items || []);
      setTotal(data.total || 0);
    } catch (err) {
      onToast("error", "Tải categories thất bại", err.message);
    } finally { setLoading(false); }
  };

  useEffect(() => { fetchData(); /* eslint-disable-next-line */ }, [page, size]);
  const onSearch = () => { setPage(1); fetchData(); };

  const openNew = () => {
    setEditId(null);
    setForm({ name: "", parentId: null, sortOrder: 0, isActive: true });
    setDialogOpen(true);
  };
  const openEdit = (r) => {
    setEditId(r.id);
    setForm({ name: r.name, parentId: r.parentId ?? null, sortOrder: r.sortOrder ?? 0, isActive: !!r.isActive });
    setDialogOpen(true);
  };

  const save = async () => {
    try {
      if (!form.name?.trim()) return onToast("warn", "Thiếu dữ liệu", "Tên danh mục bắt buộc");
      if (editId == null) {
        await http(baseUrl, "/api/categories", { method: "POST", body: JSON.stringify(form) });
        onToast("success", "Đã tạo", form.name);
      } else {
        await http(baseUrl, `/api/categories/${editId}`, { method: "PUT", body: JSON.stringify(form) });
        onToast("success", "Đã cập nhật", form.name);
      }
      setDialogOpen(false);
      fetchData();
    } catch (err) { onToast("error", "Lưu thất bại", err.message); }
  };

  const remove = (r) => {
    confirmDialog({
      message: `Xoá danh mục "${r.name}"?`, header: "Xác nhận", icon: "pi pi-exclamation-triangle",
      acceptClassName: "p-button-danger",
      accept: async () => {
        try {
          await http(baseUrl, `/api/categories/${r.id}`, { method: "DELETE" });
          onToast("success", "Đã xoá", r.name);
          fetchData();
        } catch (err) { onToast("error", "Xoá thất bại", err.message); }
      }
    });
  };

  return (
    <div className="flex flex-column gap-3">
      <ConfirmDialog />
      <div className="flex gap-2 align-items-center">
        <span className="p-input-icon-left">
          <i className="pi pi-search" />
          <InputText value={q} onChange={(e) => setQ(e.target.value)} placeholder="Tìm kiếm tên/slug" onKeyDown={(e)=> e.key==='Enter' && onSearch()} />
        </span>
        <Button icon="pi pi-search" label="Tìm" onClick={onSearch} />
        <Button icon="pi pi-refresh" className="p-button-text" onClick={fetchData} />
        <div className="flex-1" />
        <Button icon="pi pi-plus" label="Thêm" onClick={openNew} />
      </div>

      <DataTable value={rows} loading={loading} paginator rows={size} totalRecords={total}
                 first={(page - 1) * size} onPage={(e) => { setPage(Math.floor(e.first / e.rows) + 1); setSize(e.rows); }}
                 rowsPerPageOptions={[5,10,20,50]} responsiveLayout="scroll" className="shadow-1 border-round-xl">
        <Column field="id" header="#" style={{ width: 80 }} />
        <Column field="name" header="Tên" sortable />
        <Column field="slug" header="Slug" />
        <Column field="parentId" header="ParentId" style={{ width: 110 }} />
        <Column field="sortOrder" header="Thứ tự" style={{ width: 110 }} />
        <Column header="Trạng thái" body={(r) => r.isActive ? <span className="text-green-600">Active</span> : <span className="text-600">Inactive</span>} style={{ width: 130 }} />
        <Column header="Thao tác" style={{ width: 160 }} body={(r) => (
          <div className="flex gap-2">
            <Button icon="pi pi-pencil" className="p-button-sm p-button-text" onClick={() => openEdit(r)} />
            <Button icon="pi pi-trash" className="p-button-sm p-button-text p-button-danger" onClick={() => remove(r)} />
          </div>
        )} />
      </DataTable>

      <Dialog header={editId == null ? "Thêm danh mục" : "Sửa danh mục"} visible={dialogOpen} style={{ width: "36rem" }} modal onHide={() => setDialogOpen(false)}>
        <div className="flex flex-column gap-3">
          <div className="flex flex-column gap-1">
            <label className="font-medium">Tên *</label>
            <InputText value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
          </div>
          <div className="flex gap-3">
            <span className="flex-1">
              <label className="block mb-1 font-medium">Parent Id</label>
              <InputNumber inputClassName="w-full" value={form.parentId} onValueChange={(e) => setForm({ ...form, parentId: e.value })} showButtons step={1} min={0} placeholder="null" useGrouping={false} />
            </span>
            <span className="flex-1">
              <label className="block mb-1 font-medium">Thứ tự</label>
              <InputNumber inputClassName="w-full" value={form.sortOrder} onValueChange={(e) => setForm({ ...form, sortOrder: e.value ?? 0 })} showButtons step={1} useGrouping={false} />
            </span>
          </div>
          <div className="flex align-items-center gap-2">
            <label className="font-medium">Kích hoạt</label>
            <InputSwitch checked={form.isActive} onChange={(e) => setForm({ ...form, isActive: e.value })} />
          </div>
        </div>
        <div className="flex justify-content-end gap-2 mt-4">
          <Button label="Huỷ" className="p-button-text" onClick={() => setDialogOpen(false)} />
          <Button label="Lưu" icon="pi pi-save" onClick={save} />
        </div>
      </Dialog>
    </div>
  );
}
